import React, { Component } from "react";
import Habit from "./habit";

class Habits extends Component {
  state = {
    habits: [
      { id: 1, name: "Reading", count: 0 },
      { id: 2, name: "Running", count: 0 },
      { id: 3, name: "Coding", count: 0 },
    ],
  };

  //handleIncrement is variable which has reference value of the next function
  // Habit component has prop onIncrement and its value is reference of the below function.
  // 함수는 오브젝트이다. 아래 Habit의 prop으로 전달해준 건, handleIncrement 라는 변수의 값인데,
  // 이 변수가 가진 값은, 옆 함수 오브젝트의 참조값. 그러니까 주소값. 그래서 주소값을 프롭으로 전달해준 것이다.
  handleIncrement = (habit) => {
    const habits = [...this.state.habits];
    this.setState((habit) => habit.count + 1);
  };

  handleDecrement = (habit) => {
    console.log(`handleDeclrement ${habit}`);
  };

  handleDelete = (habit) => {
    console.log(`handleDelete ${habit}`);
  };

  render() {
    return (
      <ul>
        {this.state.habits.map((habit) => (
          <Habit
            key={habit.id}
            habit={habit}
            onIncrement={this.handleIncrement}
            onDecrement={this.handleDecrement}
            onDelete={this.handleDelete}
          />
        ))}
      </ul>
    );
  }
}

export default Habits;
